    public static UserAnnotation get(Server server, Long id)
        throws Exception
    {
        
		String path = "/api/userannotation/{id}.json?";
		path = path.replace("{id}",id+"");

		JSONObject json = server.doGET(path);
		UserAnnotation domain = new UserAnnotation();
		domain.build(json);
		return domain;

    
    }

